<?phpsession_start();include "../../../josys/koneksi.php";include "../../../josys/library.php";include "../../../josys/fungsi_thumb.php";include "../../../josys/fungsi_seo.php";$module=$_GET['module'];$act=$_GET['act'];// Hapusif ($module=='penerbit' AND $act=='del'){	mysql_query("DELETE FROM penerbit WHERE id_penerbit='$_GET[id]'");	header('location:../../media.php?module='.$module);}// Inputelseif ($module=='penerbit' AND $act=='input'){	if(empty($_POST['nama_penerbit'])){		echo "<script>			window.alert('Nama belum diisi!');			window.location(history.back());		</script>";	}  $penerbit_seo      = seo_title(trim($_POST['nama_penerbit']));				mysql_query("INSERT IGNORE INTO penerbit(nama_penerbit,                                    penerbit_seo)                             VALUES('$_POST[nama_penerbit]',                                   '$penerbit_seo')");				header('location:../../media.php?module='.$module);}// Update penerbitelseif ($module=='penerbit' AND $act=='update'){  $penerbit_seo      = seo_title(trim($_POST['nama_penerbit']));		mysql_query("UPDATE penerbit SET nama_penerbit 	   = '$_POST[nama_penerbit]',                                   penerbit_seo    = '$penerbit_seo'                                WHERE id_penerbit   = '$_POST[id]'");	header('location:../../media.php?module='.$module);}?>